import React from "react";

function NavToggleButton({ className, idName, iconClass }) {
  const handleToggle = () => {
    setIsToggled((prevState) => !prevState); // Toggle the state
    console.log(`Button is now: ${!isToggled ? "On" : "Off"}`); // Log the current state
  };

  return (
    <button
      onClick={handleToggle}
      type="button"
      className={className}
      id={idName}
    >
      <i className={iconClass}></i>
    </button>
  );
}

export default NavToggleButton;
